import React from 'react';
import { optimizeShopifyImage, generateSrcSet, generateSizes } from '../utils/imageOptimizer';

interface A11yImageProps extends React.ImgHTMLAttributes<HTMLImageElement> {
  alt?: string;
  fallbackAlt?: string;
  priority?: boolean;
  quality?: number;
  fill?: boolean;
  sizes?: string;
  fetchpriority?: 'high' | 'low' | 'auto';
  onLoad?: () => void;
  onError?: () => void;
  imageType?: 'hero' | 'card' | 'thumbnail' | 'gallery' | 'default';
  optimizeImage?: boolean; // ‚≠ê ‡πÄ‡∏õ‡∏¥‡∏î/‡∏õ‡∏¥‡∏î optimization (default: true)
}

export default function A11yImage({
  fallbackAlt,
  alt,
  priority,
  // quality, // Reserved for future use
  fill,
  sizes: customSizes, // ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏ä‡∏∑‡πà‡∏≠‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÑ‡∏°‡πà‡∏ä‡∏ô‡∏Å‡∏±‡∏ö generated sizes
  fetchpriority,
  className,
  style,
  loading,
  imageType = 'default',
  optimizeImage = true, // ‚≠ê ‡πÄ‡∏õ‡∏¥‡∏î optimization ‡∏ï‡∏≤‡∏°‡∏Ñ‡πà‡∏≤ default
  src,
  srcSet: customSrcSet, // ‡∏¢‡∏≠‡∏°‡∏£‡∏±‡∏ö srcSet ‡πÅ‡∏ö‡∏ö‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡πÄ‡∏≠‡∏á
  ...props
}: A11yImageProps) {
  const finalAlt = alt && alt.trim().length > 0 ? alt : (fallbackAlt ?? '‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö');

  // ‚≠ê Optimize Shopify images automatically
  let optimizedSrc = src;
  let generatedSrcSet = customSrcSet;
  let generatedSizes = customSizes;

  if (optimizeImage && src && typeof src === 'string') {
    // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏Ç‡∏ô‡∏≤‡∏î‡∏ï‡∏≤‡∏° imageType
    const widthMap = {
      hero: 1920,
      card: 1024,
      thumbnail: 240, // ‚≠ê ‡∏•‡∏î‡∏à‡∏≤‡∏Å 400 ‚Üí 240 ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÇ‡∏´‡∏•‡∏î‡πÄ‡∏£‡πá‡∏ß‡∏Ç‡∏∂‡πâ‡∏ô (thumbnail ‡πÄ‡∏•‡πá‡∏Å)
      gallery: 800,
      default: 1200,
    };
    const targetWidth = widthMap[imageType] || widthMap.default;

    // ‡∏õ‡∏£‡∏±‡∏ö‡∏Ç‡∏ô‡∏≤‡∏î‡∏£‡∏π‡∏õ
    optimizedSrc = optimizeShopifyImage(src, targetWidth, 'webp');

    // ‡∏™‡∏£‡πâ‡∏≤‡∏á srcSet ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ
    if (!customSrcSet) {
      const srcSetWidths = {
        hero: [640, 1024, 1920],
        card: [320, 640, 1024],
        thumbnail: [120, 240], // ‚≠ê ‡∏•‡∏î‡∏Ç‡∏ô‡∏≤‡∏î srcSet ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö thumbnail
        gallery: [400, 800, 1200],
        default: [640, 1024, 1920],
      };
      generatedSrcSet = generateSrcSet(src, srcSetWidths[imageType] || srcSetWidths.default);
    }

    // ‡∏™‡∏£‡πâ‡∏≤‡∏á sizes ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ
    if (!customSizes) {
      generatedSizes = generateSizes(imageType);
    }
  }

  // ‚≠ê ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤ loading attribute ‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥
  // - ‡∏ñ‡πâ‡∏≤ priority=true ‚Üí eager loading (‡πÇ‡∏´‡∏•‡∏î‡∏ó‡∏±‡∏ô‡∏ó‡∏µ)
  // - ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏ ‚Üí lazy loading (‡πÇ‡∏´‡∏•‡∏î‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÉ‡∏Å‡∏•‡πâ viewport)
  const loadingAttr = priority ? 'eager' : loading || 'lazy';

  // ‚≠ê ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤ fetchpriority attribute
  // - ‡∏£‡∏π‡∏õ priority=true ‚Üí fetchpriority="high" (‡πÇ‡∏´‡∏•‡∏î‡∏Å‡πà‡∏≠‡∏ô‡∏ó‡∏£‡∏±‡∏û‡∏¢‡∏≤‡∏Å‡∏£‡∏≠‡∏∑‡πà‡∏ô)
  // - ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏£‡∏π‡∏õ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏™‡∏≥‡∏Ñ‡∏±‡∏ç (thumbnail/gallery/card) ‡πÉ‡∏´‡πâ default ‡πÄ‡∏õ‡πá‡∏ô "low" ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏•‡∏î‡πÅ‡∏¢‡πà‡∏á‡πÅ‡∏ö‡∏ô‡∏î‡πå‡∏ß‡∏¥‡∏î‡∏ó‡πå
  let fetchPriorityAttr: 'high' | 'low' | 'auto';
  if (priority) {
    fetchPriorityAttr = 'high';
  } else if (fetchpriority) {
    fetchPriorityAttr = fetchpriority;
  } else if (imageType === 'thumbnail' || imageType === 'gallery' || imageType === 'card') {
    fetchPriorityAttr = 'low';
  } else {
    fetchPriorityAttr = 'auto';
  }

  // ‡∏ñ‡πâ‡∏≤‡πÉ‡∏ä‡πâ fill={true} ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ absolute positioning
  const finalStyle = fill
    ? {
        ...style,
        position: 'absolute' as const,
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        objectFit: 'cover' as const,
      }
    : style;

  const finalClassName = fill ? `${className || ''} absolute inset-0`.trim() : className;

  // ‚≠ê Build img attributes manually to use lowercase 'fetchpriority'
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const imgAttributes: Record<string, any> = {
    ...props,
    src: optimizedSrc,
    alt: finalAlt,
    loading: loadingAttr,
    // ‚≠ê ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤ decoding ‡∏Ñ‡πà‡∏≤‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô‡πÄ‡∏õ‡πá‡∏ô 'async' ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏£‡∏π‡∏õ non-LCP ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏•‡∏î‡∏ö‡∏•‡πá‡∏≠‡∏Å‡∏Å‡∏≤‡∏£‡πÄ‡∏£‡∏ô‡πÄ‡∏î‡∏≠‡∏£‡πå
    decoding: props && 'decoding' in props && props.decoding ? props.decoding : 'async',
    style: finalStyle,
    className: finalClassName,
  };

  // ‡πÄ‡∏û‡∏¥‡πà‡∏° srcSet ‡πÅ‡∏•‡∏∞ sizes ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ
  if (generatedSrcSet) {
    imgAttributes.srcSet = generatedSrcSet;
  }
  if (generatedSizes) {
    imgAttributes.sizes = generatedSizes;
  }

  // Add fetchpriority as lowercase attribute (HTML spec)
  if (fetchPriorityAttr !== 'auto') {
    imgAttributes['fetchpriority'] = fetchPriorityAttr;
  }

  // eslint-disable-next-line jsx-a11y/alt-text
  return <img {...imgAttributes} />;
}

/**
 * Facebook Pixel Component - Interaction-based Lazy Loading
 * ‡πÇ‡∏´‡∏•‡∏î‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏°‡∏µ‡∏õ‡∏è‡∏¥‡∏™‡∏±‡∏°‡∏û‡∏±‡∏ô‡∏ò‡πå‡∏Å‡∏±‡∏ö‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏ß‡πá‡∏ö (scroll, click, touch) ‡∏´‡∏£‡∏∑‡∏≠‡∏£‡∏≠ 5 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ
 * ‡∏•‡∏î JavaScript ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô, ‡∏•‡∏î TBT ‡πÅ‡∏•‡∏∞‡πÄ‡∏û‡∏¥‡πà‡∏° Performance Score
 * ‡∏õ‡∏£‡∏∞‡∏´‡∏¢‡∏±‡∏î‡∏û‡∏∑‡πâ‡∏ô‡∏ó‡∏µ‡πà ~48 KiB ‡∏Ç‡∏≠‡∏á unused JavaScript
 */

'use client';
import { useEffect } from 'react';

export default function FacebookPixel() {
  useEffect(() => {
    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô production ‡πÅ‡∏•‡∏∞‡∏°‡∏µ Pixel ID
    const pixelId = process.env.NEXT_PUBLIC_FACEBOOK_PIXEL_ID;
    const isProduction = process.env.NODE_ENV === 'production';

    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ Pixel ID ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà production ‡πÉ‡∏´‡πâ‡∏Ç‡πâ‡∏≤‡∏°‡∏Å‡∏≤‡∏£‡πÇ‡∏´‡∏•‡∏î
    if (!pixelId || !isProduction) {
      return;
    }

    let loaded = false;
    let timeoutId = null;

    const loadFacebookPixel = () => {
      // ‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Å‡∏≤‡∏£‡πÇ‡∏´‡∏•‡∏î‡∏ã‡πâ‡∏≥
      if (loaded || window.fbq) return;
      loaded = true;

      // ‡∏¢‡∏Å‡πÄ‡∏•‡∏¥‡∏Å timeout ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ
      if (timeoutId) clearTimeout(timeoutId);

      // ‡∏™‡∏£‡πâ‡∏≤‡∏á fbq function stub
      window.fbq = function () {
        if (window.fbq.callMethod) {
          window.fbq.callMethod.apply(window.fbq, arguments);
        } else {
          window.fbq.queue.push(arguments);
        }
      };
      if (!window._fbq) window._fbq = window.fbq;
      window.fbq.push = window.fbq;
      window.fbq.loaded = true;
      window.fbq.version = '2.0';
      window.fbq.queue = [];

      // ‡πÇ‡∏´‡∏•‡∏î Facebook Pixel script
      const script = document.createElement('script');
      script.src = 'https://connect.facebook.net/en_US/fbevents.js';
      script.async = true;
      script.defer = true;

      script.onload = () => {
        // Initialize Facebook Pixel
        if (window.fbq) {
          window.fbq('init', pixelId);
          window.fbq('track', 'PageView');
        }
      };

      script.onerror = () => {
        // Silent fail - Facebook Pixel is non-critical
        // eslint-disable-next-line no-console
        console.warn('Facebook Pixel failed to load - check traffic permissions');
      };

      document.body.appendChild(script);
    };

    // Event listeners ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡πÇ‡∏ï‡πâ‡∏ï‡∏≠‡∏ö‡∏Ç‡∏≠‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ
    const interactionEvents = ['scroll', 'click', 'touchstart', 'mousemove', 'keydown'];

    const handleInteraction = () => {
      loadFacebookPixel();
      // ‡∏•‡∏ö event listeners ‡∏´‡∏•‡∏±‡∏á‡∏à‡∏≤‡∏Å‡πÇ‡∏´‡∏•‡∏î‡πÅ‡∏•‡πâ‡∏ß
      interactionEvents.forEach(event => {
        window.removeEventListener(event, handleInteraction);
      });
    };

    // ‡∏ï‡∏¥‡∏î‡∏ï‡∏±‡πâ‡∏á event listeners
    interactionEvents.forEach(event => {
      window.addEventListener(event, handleInteraction, { passive: true, once: true });
    });

    // Fallback: ‡πÇ‡∏´‡∏•‡∏î‡∏´‡∏•‡∏±‡∏á 5 ‡∏ß‡∏¥‡∏ô‡∏≤‡∏ó‡∏µ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡πÇ‡∏ï‡πâ‡∏ï‡∏≠‡∏ö
    timeoutId = setTimeout(() => {
      loadFacebookPixel();
      // ‡∏•‡∏ö event listeners
      interactionEvents.forEach(event => {
        window.removeEventListener(event, handleInteraction);
      });
    }, 5000);

    return () => {
      if (timeoutId) clearTimeout(timeoutId);
      interactionEvents.forEach(event => {
        window.removeEventListener(event, handleInteraction);
      });
    };
  }, []);

  // Noscript fallback
  const pixelId = process.env.NEXT_PUBLIC_FACEBOOK_PIXEL_ID;
  if (!pixelId) return null;

  return (
    <noscript>
      <img
        height="1"
        width="1"
        style={{ display: 'none' }}
        src={`https://www.facebook.com/tr?id=${pixelId}&ev=PageView&noscript=1`}
        alt=""
      />
    </noscript>
  );
}

/**
 * Image Gallery Manager Component
 * Copyright (c) 2025 Chiangmai Used Car (‡∏Ñ‡∏£‡∏π‡∏´‡∏ô‡∏∂‡πà‡∏á‡∏£‡∏ñ‡∏™‡∏ß‡∏¢)
 * ‡∏Ñ‡∏≠‡∏°‡πÇ‡∏û‡πÄ‡∏ô‡∏ô‡∏ï‡πå‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÉ‡∏ô‡∏ö‡∏ó‡∏Ñ‡∏ß‡∏≤‡∏°
 */

import { useState, useEffect } from 'react';
import {
  getArticleImages,
  deleteImageFromStorage,
  isValidImageFile,
  resizeImage,
  saveImageToStorage,
} from '../lib/imageUtils';

const ImageGallery = ({ articleId, onImageSelect, selectMode = false }) => {
  const [images, setImages] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [selectedImages, setSelectedImages] = useState([]);
  const [showImageOptions, setShowImageOptions] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);
  const [showAdvancedOptions, setShowAdvancedOptions] = useState(false);

  // ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏õ‡∏¥‡∏î‡∏´‡∏ô‡πâ‡∏≤
  useEffect(() => {
    const loadImages = () => {
      const articleImages = getArticleImages(articleId);
      // ‡πÉ‡∏ä‡πâ Shopify CDN ‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á (‡πÑ‡∏°‡πà‡∏ú‡πà‡∏≤‡∏ô Cloudflare)
      setImages(articleImages);
    };

    if (articleId) {
      loadImages();
    }
  }, [articleId]);

  const loadImages = () => {
    const articleImages = getArticleImages(articleId);
    // ‡πÉ‡∏ä‡πâ Shopify CDN ‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á (‡πÑ‡∏°‡πà‡∏ú‡πà‡∏≤‡∏ô Cloudflare)
    setImages(articleImages);
  };

  // ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
  const handleFileUpload = async event => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    setUploading(true);

    try {
      for (const file of files) {
        if (!isValidImageFile(file)) {
          alert(`‡πÑ‡∏ü‡∏•‡πå ${file.name} ‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏ó‡∏µ‡πà‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö ‡∏´‡∏£‡∏∑‡∏≠‡∏°‡∏µ‡∏Ç‡∏ô‡∏≤‡∏î‡πÉ‡∏´‡∏ç‡πà‡πÄ‡∏Å‡∏¥‡∏ô 5MB`);
          continue;
        }

        // ‡∏õ‡∏£‡∏±‡∏ö‡∏Ç‡∏ô‡∏≤‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
        const resizedFile = await resizeImage(file);

        // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
        await saveImageToStorage(resizedFile || file, articleId);
      }

      // ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÉ‡∏´‡∏°‡πà
      loadImages();
      alert('‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!');
    } catch (error) {
      alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  // ‡∏•‡∏ö‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
  const handleDeleteImage = imageName => {
    if (confirm('‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏ô‡∏µ‡πâ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà?')) {
      deleteImageFromStorage(imageName);
      loadImages();
    }
  };

  // ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÅ‡∏ó‡∏£‡∏Å‡πÉ‡∏ô‡∏ö‡∏ó‡∏Ñ‡∏ß‡∏≤‡∏°
  const handleSelectImage = image => {
    if (selectMode) {
      // ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö selectMode (‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏£‡∏π‡∏õ‡∏õ‡∏Å) ‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö data URL
      onImageSelect?.(image.data);
    } else {
      // ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Rich Text Editor ‡πÅ‡∏™‡∏î‡∏á‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏Å‡∏≤‡∏£‡πÅ‡∏ó‡∏£‡∏Å
      setCurrentImage(image);
      setShowImageOptions(true);
    }
  };

  // Quick Insert - ‡πÅ‡∏ó‡∏£‡∏Å‡∏£‡∏π‡∏õ‡∏î‡πâ‡∏ß‡∏¢‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏°‡∏≤‡∏ï‡∏£‡∏ê‡∏≤‡∏ô
  const quickInsertImage = (image, preset) => {
    const presets = {
      'small-left': {
        size: 'small',
        alignment: 'left',
        caption: '',
      },
      'small-right': {
        size: 'small',
        alignment: 'right',
        caption: '',
      },
      'medium-center': {
        size: 'medium',
        alignment: 'center',
        caption: '',
      },
      'large-center': {
        size: 'large',
        alignment: 'center',
        caption: '',
      },
    };

    const config = presets[preset];
    if (config) {
      setCurrentImage(image);
      insertImageWithOptions(config.size, config.alignment, config.caption);
    }
  };

  // ‡πÅ‡∏ó‡∏£‡∏Å‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏î‡πâ‡∏ß‡∏¢‡∏Ç‡∏ô‡∏≤‡∏î‡πÅ‡∏•‡∏∞‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏ó‡∏µ‡πà‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
  const insertImageWithOptions = (size, alignment, caption = '') => {
    if (!currentImage) return;

    const sizeStyles = {
      small: 'max-width: 300px; width: 100%;',
      medium: 'max-width: 600px; width: 100%;',
      large: 'max-width: 100%; width: 100%;',
      thumbnail: 'max-width: 150px; width: 100%;',
      fullwidth: 'width: 100%;',
    };

    const alignmentStyles = {
      left: 'float: left; margin: 0 30px 20px 0; clear: left;',
      right: 'float: right; margin: 0 0 20px 30px; clear: right;',
      center: 'display: block; margin: 30px auto; clear: both;',
      inline: 'display: inline-block; margin: 15px 10px; vertical-align: top;',
    };

    const baseStyle = `${sizeStyles[size]} ${alignmentStyles[alignment]} height: auto; border-radius: 8px; box-shadow: 0 2px 8px rgba(0,0,0,0.1);`;

    let imgHTML;

    if (caption) {
      // ‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö Figure + Figcaption (‡∏°‡∏≤‡∏ï‡∏£‡∏ê‡∏≤‡∏ô HTML5)
      imgHTML = `<figure style="margin: 30px 0; ${alignment === 'center' ? 'text-align: center;' : ''} ${alignment === 'left' ? 'float: left; margin: 0 30px 20px 0; clear: left;' : ''} ${alignment === 'right' ? 'float: right; margin: 0 0 20px 30px; clear: right;' : ''}">
  <img src="${currentImage.data}" alt="${currentImage.name}" style="${sizeStyles[size]} height: auto; border-radius: 8px; box-shadow: 0 2px 8px rgba(0,0,0,0.1); display: block;" />
  <figcaption style="font-size: 14px; color: #666; margin-top: 8px; font-style: italic; padding: 0 10px; line-height: 1.4;">${caption}</figcaption>
</figure>`;
    } else {
      // ‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö IMG ‡∏ò‡∏£‡∏£‡∏°‡∏î‡∏≤
      imgHTML = `<img src="${currentImage.data}" alt="${currentImage.name}" style="${baseStyle}" />`;
    }

    onImageSelect?.(imgHTML);
